// Letters
var letters = "ابتثجحخدذرزسشصضطظعغفقكلمنهويةؤءئإأآ1234567890-abcdefghijklmnopqrstuvwxyz";
// Get Array From Letters
var lettersArray = Array.from(letters);
// Select Letters Container
var lettersContainer = document.querySelector(".letters");

// Generate Letters
lettersArray.forEach(letter => {
    // Create Span
    var span = document.createElement("span");
    // Create Letter Text Node
    var theLetter = document.createTextNode(letter);
    // Append The Letter To Span
    span.appendChild(theLetter);
    // Add Class On Span
    span.className = "letter-box";
    // Append Letter To The Letters Container
    lettersContainer.appendChild(span);
});
