// Get Random Properety

var allKeys = Object.keys(words);
// Random Number Depend On Keys Length
var randomPropNumber = Math.floor(Math.random() * allKeys.length);
// Category
var randomPropName = allKeys[randomPropNumber];
// Category Words
var randomPropValue = words[randomPropName];
// Random Number Depend On Words
var randomValueNumber = Math.floor(Math.random() * randomPropValue.length);
// The Chosen Words
var randomValueValue = randomPropValue[randomValueNumber];
// Set Category Info
document.querySelector(".game-info .category span").innerHTML = randomPropName;
// Select Letters Guess Element

var lettersGuessContainer = document.querySelector(".letters-guess");

// Convert Chosen Word To  Array

var lettersAndSpace = Array.from(randomValueValue);

// Create Spans Depend On Word
lettersAndSpace.forEach(letter => {

    // Create Empty Span
    var emptySpan = document.createElement("span");
    // If Letters Is Space 
    if (letter === ' ') {
        // Add Class To The Span
        emptySpan.className = 'with-space';
    }
    // Append Span To The Letters Guess Container
    lettersGuessContainer.appendChild(emptySpan);
});
// Select Guess Spans

var guessSpans = document.querySelectorAll(".letters-guess span");
// Set Weong Attempt
var wrongAttemps = 0;
// Select The Draw Element
var theDraw = document.querySelector(".hangman-draw");
// Handle Clicking On Letters
document.addEventListener("click", (e) => {
    // Set The Chose Status
    var theStatus = false;
    if (e.target.className === 'letter-box') {
        e.target.classList.add("clicked");

        // Get Clicked Letter
        var theClickedLetter = e.target.innerHTML.toLowerCase();
        console.log(theClickedLetter);
        // The Chosen Word
        var theChosenWord = Array.from(randomValueValue.toLowerCase())
        theChosenWord.forEach((wordLetter, Wordindex) => {
            // If The Clicked Letter Equal To One Of The Chosen Word Letter
            if (theClickedLetter == wordLetter) {
                // Set Status To Correct
                theStatus = true
                // Loop On All Guess Spans
                guessSpans.forEach((span, spanIndex) => {

                    if (Wordindex === spanIndex) {

                        span.innerHTML = theClickedLetter;

                    }

                })


            }
        });
        // Outside Loop
        // If The Letter Is Wrong
        if (theStatus !== true) {
            // Increase The Wrong Attemps
            wrongAttemps++;

            // Add Class On The Draw Element
            theDraw.classList.add(`wrong-${wrongAttemps}`);
            // Play Fail Sound
            document.getElementById("fail").play();

            if (wrongAttemps === 8) {
                endGame();
                lettersContainer.classList.add("finished");
            }
        } else {
            // Play Success Sound
            document.getElementById("success").play();
        }
    }
});
// End Game Function
function endGame() {
    // Create Popup Div
    var div = document.createElement("div");
    // Create Text 
    var divText = document.createTextNode(`Game Over, The Word Is ${randomValueValue}`);
    // Append Text To Div
    div.appendChild(divText);
    // Add Class On Div
    div.className = 'popup';
    // Append To The Body
    document.body.appendChild(div);
}
function success() {
    // Create Popup Div
    var div1 = document.createElement("div");
    // Create Text 
    var div1Text = document.createTextNode("You Win");
    // Append Text To Div
    div1.appendChild(div1Text);
    // Add Class On Div
    div1.className = 'popup';
    // Append To The Body
    document.body.appendChild(div1);
}